mapper.insert:
insert into article (openid, time, article
  )
values (#{openid,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}, #{article,jdbcType=LONGVARCHAR}
  )

mapper.update:
update article
set time = #{time,jdbcType=TIMESTAMP},
  article = #{article,jdbcType=LONGVARCHAR}
where openid = #{openid,jdbcType=VARCHAR}

mapper.select:
select openid, time, article
from article
where openid = #{openid,jdbcType=VARCHAR}

select openid, time, article
from article
where openid = #{openid,jdbcType=VARCHAR}
